const generateOrderId = () => {
    orderId.value = Date.now();
};

let ids = reactive([]);
// 全选按钮点击事件
const toggleSelectAll = () => {
    ids = odt.value;
    selectAll.value = !selectAll.value;
    odt.value.forEach(item => {
        item.checked = selectAll.value;
        if (selectAll.value) {
            if (!ids.includes(item)) {
                ids.push(item);
            }
        } else {
            // 清空选中数组
            ids = [];
        }
    });
    console.log("选中的所有数据", ids);
};

// 订单选中状态改变事件
const toggleItemSelect = (index,order) => {
    // 更新订单选中状态
    odt.value[index].checked = !odt.value[index].checked;

    // 根据订单的选中状态添加或移除订单
    if (odt.value[index].checked) {
        if (!ids.includes(order)) {
            ids.push(order);
        }
    } else {
        // 移除未选中的订单
        const indexToRemove = ids.findIndex(item => item.cart_id === order.cart_id);
        if (indexToRemove !== -1) {
            ids.splice(indexToRemove, 1);
        }
    }

    // 检查是否所有订单都被选中
    selectAll.value = odt.value.every(item => item.checked);

    console.log("选中的所有数据", ids);
};


