// 计算选中商品的总价格
const calculateTotalPrice = () => {
    return odt.value.reduce((total, item) => {
        if (item.checked) {
            orders.total_amount = total + item.products.price * item.quantity;
            return total + item.products.price * item.quantity;
        }
        return total;
    }, 0);
};

//生成随机数
function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min) + min); //含最大值，含最小值
}

