<script setup lang="ts">
import imageUrlw from './public/img/背景.png';
import imageT from './public/img/34.jpg';
import { ref } from 'vue';
import { ElMessage } from 'element-plus';
import { Plus } from '@element-plus/icons-vue';

import type { UploadProps } from 'element-plus';
import { Swiper, SwiperSlide } from 'vue-awesome-swiper'
import 'swiper/swiper-bundle.css';
import axios from "axios";
import {localGet} from "../utils";


const images = [
    new URL('../view/public/img/pupu1.png', import.meta.url),
    new URL('../view/public/img/pupu2.png', import.meta.url),
    new URL('../view/public/img/pupu3.png', import.meta.url)
];
const swiperOptions = ref({
    autoplay: {
        delay: 2000,
        disableOnInteraction: false,
    },
    loop: true,
    // 其他配置项
});
const imageUrl = ref(imageUrlw);
const newImageUrl = ref('');

const handleAvatarSuccess: UploadProps['onSuccess'] = (response, uploadFile) => {
    imageUrl.value = URL.createObjectURL(uploadFile.raw);
}

const beforeAvatarUpload: UploadProps['beforeUpload'] = (rawFile) => {
    if (rawFile.type !== 'image/jpeg') {
        ElMessage.error('Avatar picture must be JPG format!');
        return false;
    } else if (rawFile.size / 1024 / 1024 > 10) {
        ElMessage.error('Avatar picture size can not exceed 10MB!');
        return false;
    }
    return true;
}

const users = ref([]);
const fetchOrders = async () => {
    let rid=localGet('token')
    const decoded = parseJwt(rid);
    // 获取sub字段的值
    const sub = decoded.sub;
    const subObj = JSON.parse(sub.replace(/'/g, '"')); // 替换单引号为双引号
    try {
        const response = await axios.get('http://localhost:88/users/selectUsers?user_id='+subObj.user_id);
        users.value = response.data;
        console.log("hdhwdh",users.value)
    } catch (error) {
        console.error('There was an error!', error);
    }
};

// 在组件挂载时获取数据
fetchOrders();
function parseJwt(token) {


    // 使用.split()方法将JWT分割成三个部分
    let base64Url = token.split('.')[1];

    // 将URL安全的Base64字符替换为标准Base64字符
    let base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');

    // 计算并处理padding
    const padding = base64.length % 4;
    if (padding) {
        base64 += '='.repeat(4 - padding);
    }

    // 使用atob解码Base64字符串，并处理unicode字符
    let payload = decodeURIComponent(atob(base64).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
    }).join(''));

    return JSON.parse(payload);
}

</script>

